package io.github.rosemoe.editor.core.extension;

import android.os.Parcel;
import android.os.Parcelable;

import java.util.HashMap;

import io.github.rosemoe.editor.core.CodeEditor;
import io.github.rosemoe.editor.core.extension.events.Event;
import io.github.rosemoe.editor.core.extension.events.EventDestination;
import io.github.rosemoe.editor.core.extension.events.EventQueue;
import io.github.rosemoe.editor.core.extension.events.EventSource;
import io.github.rosemoe.editor.core.util.Logger;


/**
 * With this generic class you can add widgets and plugins to the editor.
 * Adding widget will make the life easier to plugins developpers.
 *
 * The fact to separate Plugin WidgetController allow use to control by  priority access to resources given to plugins.
 * So you can think the widget as a low level Plugin.
 * And the plugin as an higher level plugin.
 *
 */
public class Extension implements EventSource, EventDestination, Comparable, Parcelable {

    /**
     * Plugin priority declaration : WARNING they should be putted from low to high priority.
     * that is plugin can be given a priority.
     */
    public static final int PRIORITY_LOW = -1000;
    public static final int PRIORITY_STD = 0;
    public static final int PRIORITY_HIGH = 1000;
    public int priorityRing = PRIORITY_STD;

    private HashMap<Class, Boolean> subscribedEventTypes = new HashMap<>();

    /**
     * Name of the extension.
     */
    public  String name       = "default";
    /**
     * Extension's description.
     */
    public String description = "default description";
    /**
     * Image for the extension.
     */
    public String image       = DEFAULT_IMG;
    public final static String DEFAULT_IMG = "";

    @Override
    public int compareTo(Object o) {
        if ( o instanceof Extension) {
            Extension p = (Extension) o;
            if ( priorityRing < p.priorityRing ) {
                return -1;
            } else if(priorityRing > p.priorityRing ) {
                return 1;
            }
            return 0;
        }
        // ex: compareTo null will result in null to be processed with less priority.
        return 1;
    }

    public boolean enabled = true;
    /**
     * Is the plugin enabled
     * @param state
     */
    public void setEnabled(boolean state) {
        enabled = state;
    }
    public boolean isEnabled() {
        return enabled;
    }
    public boolean isDisabled() { return ! enabled; }
    public boolean toggleIsEnabled() {
        setEnabled(!isEnabled());
        return isEnabled();
    }

    @Override
    public void writeToParcel(Parcel dest, int flags) {
        Logger.debug("Writing parcel :",name,description);
        dest.writeString(name);
        dest.writeString(description);
        dest.writeBoolean(isEnabled());
    }
    @Override
    public int describeContents() {
        return 0;
    }

    public static final Parcelable.Creator<Extension> CREATOR
            = new Parcelable.Creator<Extension>() {
        public Extension createFromParcel(Parcel in) {
            return new Extension(in);
        }

        public Extension[] newArray(int size) {
            return new Extension[size];
        }
    };

    private Extension(Parcel in) {
        Logger.debug("Reading parcel");
       editor = null;
       name = in.readString();
       description = in.readString();
       setEnabled(in.readBoolean());
    }


    /**
     * Override this method of the widget to provide action in the widget on event emission.
     * @param e
     */
    protected void handleEventEmit(Event e) { }

    @Override
    public void emit(Event e) {
        if ( isDisabled() ) {
            Logger.debug("Cannot emit as this extension is disabled !");
            return;
        }
        new Thread() {
            @Override
            public void run() {
                super.run();
                handleEventEmit(e);
            }
        }.start();
    }


    private EventQueue dispatchQueue = new EventQueue() {
        @Override
        public void handlePolling(Event e) {
            handleEventDispatch(e,e.getSubType());
        }
    };
    @Override
    public void dispatch(Event e) {
        if ( isDisabled() ) {
            Logger.debug("Cannot emit as this extension is disabled !");
            return;
        }
        if( issubscribed(e.getClass()) ) {
            Logger.debug("Inserting event in the pq");
            e.dump("      ");
            dispatchQueue.add(e);
        }
    }

    @Override
    public void subscribe(Class type) {
        subscribedEventTypes.put(type,true);
    }

    @Override
    public void unsubscribe(Class type) {
        subscribedEventTypes.put(type,false);
    }

    @Override
    public boolean issubscribed(Class type) {
        return subscribedEventTypes.containsKey(type);
    }

    /**
     * Override this method to execute action when a given event is dispatched.
     * @param e
     */
    protected void handleEventDispatch(Event e, String subtype) { }

    public final CodeEditor editor;
    public Extension(CodeEditor editor) {
        this.editor = editor;
    }

    public String getPrefixedColor(String name) {
        return this.name + "." + name;
    }
    public void registerColor(String name, Object value) {
        editor.colorManager.register(getPrefixedColor(name),value);
    }
    public Integer getColor(String name) {
        return editor.colorManager.getColor(name);
    }
    public Integer getPrivateColor(String name) {
        return editor.colorManager.getColor(getPrefixedColor(name));
    }

}
